func TestSm2(t *testing.T) {
	CryptoType = CRYPTO_SM2_SM3_SM4
	for i := 0; i < 1000; i++ {
		priv, err := crypto.GenerateKey()
		if err != nil {
			log.Fatal(err)
		}
		fmt.Printf("%v\n", priv.Curve.IsOnCurve(priv.X, priv.Y))
		pub := &priv.PublicKey
		fmt.Println(hex.EncodeToString(crypto.FromECDSAPub(pub)))
		msg := []byte("")
		d0, err := Encrypt(pub, msg, nil, nil)
		if err != nil {
			fmt.Printf("Error: failed to encrypt %s: %v\n", msg, err)
			return
		}
		fmt.Printf("Cipher text = %v\n", d0)
		d1, err := Decrypt(priv, d0, nil, nil)
		if err != nil {
			fmt.Printf("Error: failed to decrypt: %v\n", err)
		}
		fmt.Printf("clear text = %s\n", d1)

		msg, _ = ioutil.ReadFile("ifile")
		//Keccak256(msg)
		sign, err := crypto.Sign(crypto.Keccak256(msg), priv)
		if err != nil {
			log.Fatal(err)
		}

		signdata := sign
		_, err = crypto.Ecrecover(crypto.Keccak256(msg), signdata)
		if err != nil {
			fmt.Printf("VerifyTransaction error\n")
		} else {
			fmt.Printf("VerifyTransaction ok\n")
		}
		ok := crypto.VerifySignature(crypto.FromECDSAPub(pub), crypto.Keccak256(msg), signdata)
		//ValidateSignatureValues(signdata[65],sign[])
		if ok != true {
			fmt.Printf("Verify error\n")
		} else {
			fmt.Printf("Verify ok\n")
		}
	}
}

