func TestSm2Time(t *testing.T) {
	CryptoType = CRYPTO_SM2_SM3_SM4
	priv, err := crypto.GenerateKey()
	if err != nil {
		log.Fatal(err)
	}
	msg := []byte("")
	hash := crypto.Keccak256(msg)
	signdata, err := crypto.Sign(hash, priv)
	if err != nil {
		log.Fatal(err)
	}

	t0 := time.Now()
	for i := 0; i < 5000; i++ {
		_, err = crypto.Ecrecover(hash, signdata)
		if err != nil {
			fmt.Printf("VerifyTransaction 0 error\n")
		}
	}
	t11 := time.Since(t0)
	fmt.Println("t", t11, " ", t11/5000)

	t0 = time.Now()
	for i := 0; i < 5000; i++ {
		ok := crypto.VerifySignature(crypto.FromECDSAPub(&priv.PublicKey), hash, signdata)
		if ok != true {
			fmt.Printf("VerifyTransaction 0 error\n")
		}
	}
	t11 = time.Since(t0)
	fmt.Println("t", t11, " ", t11/5000)
}

