func TestSm2Recover(t *testing.T) {
	CryptoType = CRYPTO_S256_SH3_AES
	priv, err := crypto.GenerateKey()
	if err != nil {
		log.Fatal(err)
	}
	fmt.Println("t", hex.EncodeToString(crypto.FromECDSAPub(&priv.PublicKey)))
	msg := []byte("")
	hash := crypto.Keccak256(msg)
	signdata, err := crypto.Sign(hash, priv)
	if err != nil {
		log.Fatal(err)
	}

	pk, err := crypto.SigToPub(hash, signdata)
	if err != nil {
		log.Fatal(err)
	}
	fmt.Println("t", hex.EncodeToString(crypto.FromECDSAPub(pk)))
	pk1, err := crypto.Ecrecover(hash, signdata)
	fmt.Println("pk", hex.EncodeToString(pk1))

}
